custom_templates:
{"alert": {"name": "警告模板", "description": "系统警告和错误消息模板", "html_content": "<!DOCTYPE html>
<html>
<head>
    <meta charset="utf-8">
    <style>
        body {
            font-family: 'Microsoft YaHei', sans-serif;
            background: linear-gradient(135deg, #ff6b6b 0%, #ee5a24 100%);
            margin: 0;
            padding: 20px;
            min-height: 100vh;
            display: flex;
            align-items: center;
            justify-content: center;
        }
        .alert-card {
            background: white;
            border-radius: 15px;
            padding: 30px;
            box-shadow: 0 10px 30px rgba(0,0,0,0.3);
            max-width: 600px;
            width: 100%;
            border-left: 5px solid #ff6b6b;
        }
        .alert-icon {
            font-size: 48px;
            color: #ff6b6b;
            text-align: center;
            margin-bottom: 20px;
        }
        .alert-title {
            font-size: 24px;
            font-weight: bold;
            color: #d63031;
            margin-bottom: 15px;
            text-align: center;
        }
        .alert-message {
            font-size: 16px;
            color: #2d3436;
            line-height: 1.6;
            margin-bottom: 20px;
            text-align: center;
        }
        .alert-footer {
            font-size: 12px;
            color: #b2bec3;
            text-align: center;
            border-top: 1px solid #ddd;
            padding-top: 15px;
        }
    </style>
</head>
<body>
    <div class="alert-card">
        <div class="alert-icon">⚠️</div>
        <div class="alert-title">{{title | default('系统警告')}}</div>
        <div class="alert-message">{{message | default('检测到系统异常')}}</div>
        <div class="alert-footer">{{timestamp | default('刚刚')}} | 级别: {{level | default('WARNING')}}</div>
    </div>
</body>
</html>", "render_width": 800, "render_quality": "high"}}
